import java.util.*

// 316. Remove Duplicate Letters
// 1081. Smallest Subsequence of Distinct Characters
fun removeDuplicateLetters(s: String): String {
    val chars = mutableSetOf<Char>()
    val existsLater = Array(s.length) { false }

    // init existsLater
    for (i in s.length - 1 downTo 0) {
        if (chars.contains(s[i])) {
            existsLater[i] = true
        }
        chars.add(s[i])
    }

    val used = mutableMapOf<Char, Boolean>()
    val stack = Stack<Char>()
    stack.push(s[0])
    used[s[0]] = existsLater[0]
    for (i in 1 until s.length) {
        if (!used.contains(s[i])) {
            while (stack.isNotEmpty() && stack.peek() > s[i] && used[stack.peek()]!!) {
                used.remove(stack.pop())
            }
            stack.push(s[i])
        }
        used[s[i]] = existsLater[i]
    }

    return stack.toMutableList().joinToString("")
}

// Slower solution, which creates each of the potential substrings
fun removeDuplicateLetters2(s: String): String {
    val used = mutableSetOf<Char>()
    var lists = mutableSetOf<MutableList<Char>>()

    lists.add(mutableListOf())
    s.forEach { c ->
        if (used.contains(c)) {
            val newLists = mutableSetOf<MutableList<Char>>()
            lists.forEach { list ->
                val new = list.minus(c).plus(c).toMutableList()
                newLists.add(new)
            }
            lists.addAll(newLists)
        } else {
            lists.forEach { list ->
                list.add(c)
            }
            used.add(c)
        }
    }

    val resultList = lists.first()
    var result = resultList.joinToString("")
    lists.remove(resultList)
    lists.forEach { list ->
        val str = list.joinToString("")
        if (str < result) {
            result = str
        }
    }

    return result
}

fun main(args: Array<String>) {
    println(removeDuplicateLetters("bcabc"))
    println(removeDuplicateLetters("cbacdcbc"))
    println(removeDuplicateLetters(""))
    println(removeDuplicateLetters("bxshkpdwcsjdbikywvioxrypfzfbppydfilfxxtouzzjxaymjpmdoevv"))
